interface.definition:
a!localVariables(
  local!fechaI,
  local!fechaI_aux,
  local!fechaF,
  local!fechaF_aux,
  local!tipoReporte,

  {
    #"SYSTEM_SYSRULES_columnsLayout"(
      columns: {
        #"SYSTEM_SYSRULES_columnLayout"(
          contents: {
            #"SYSTEM_SYSRULES_boxLayout"(
              label: "Reporte de Operaciones ",
              contents: {
                #"SYSTEM_SYSRULES_columnsLayout"(
                  columns: {
                    #"SYSTEM_SYSRULES_columnLayout"(
                      contents: {
                        #"SYSTEM_SYSRULES_dateField"(

                          label: "Fecha Inicial",

                          labelPosition: "ADJACENT",
                          value: local!fechaI, 



                          saveInto: {
                            local!fechaI,
                            a!save(
                              local!fechaI_aux,
                              if(a!isNotNullOrEmpty( local!fechaI),
                              text( date(year((local!fechaI)),month((local!fechaI)), day((local!fechaI))),"dd/mm/yyyy"),
                              null
                              )

                            ),

                          },
                          helpTooltip: if(
                            a!isNullOrEmpty(local!fechaF),
                            "Se mostrarán los reportes de la fecha seleccionada",
                            ""
                          )
                        )
                      }
                    ),
                    #"SYSTEM_SYSRULES_columnLayout"(
                      contents: {
                        #"SYSTEM_SYSRULES_dateField"(
                          label: "Fecha Final",
                          labelPosition: "ADJACENT",
                          value: local!fechaF,
                          helpTooltip: if(
                            a!isNullOrEmpty(local!fechaI),
                            "Se mostrarán los reportes hasta la fecha",
                              "Se mostrarán los reportes desde la fecha de inicio hasta la fecha final seleccionada seleccionada"
                          ),
                          saveInto: {
                            local!fechaF,
                            a!save(
                              local!fechaF_aux,
                              if(a!isNotNullOrEmpty( local!fechaF),
                              text( date(year((local!fechaF)),month((local!fechaF)), day((local!fechaF))),"dd/mm/yyyy"),
                              null
                              )
                            ),

                          },
                          validations: {
                            if(
                              and(a!isNotNullOrEmpty(local!fechaF), a!isNotNullOrEmpty(local!fechaI)),
                              if(
                                local!fechaI > local!fechaF,
                                "La fecha inicial no puede ser mayor que la fecha de final",
                                ""
                              ),
                              ""
                            )
                          }
                        )
                      }
                    ),
                    #"SYSTEM_SYSRULES_columnLayout"(
                      contents: {
                        #"SYSTEM_SYSRULES_dropdownFieldByIndex_v1"(
                          label: "Tipo de Reporte:",
                          labelPosition: "ADJACENT",
                          placeholder: "Tipo de Reporte",
                          value: local!tipoReporte,
                          choiceLabels: {"Reportes", "Reportes Aprobados", "Reportes Eliminados"},
                          helpTooltip: if(
                            a!isNullOrEmpty(local!tipoReporte),
                            "Seleccione el tipo de reporte",
                            ""
                          ),
                          saveInto: {
                            local!tipoReporte
                          },
                        )
                      }
                    ),
                    #"SYSTEM_SYSRULES_columnLayout"(
                      contents: {
                        #"SYSTEM_SYSRULES_ButtonArrayLayout"(
                          buttons: {
                            #"SYSTEM_SYSRULES_ButtonWidget"(
                              label: "Buscar",
                              icon: "search",
                              size: "SMALL",
                              loadingIndicator: true,
                              saveInto: {
                                /*a!save(ri!fechaInicio, tostring(local!fechaI)),*/
                                /*a!save(ri!fechaFin, tostring(local!fechaF)),*/
                                a!save(ri!fechaInicio,local!fechaI),
                                a!save(ri!fechaFin, local!fechaF),
                                a!save(ri!fechaalDividida, split(tostring(local!fechaF_aux), "/")),
                                a!save(ri!fechaDividida, split(tostring(local!fechaI_aux), "/")),
                                a!save(ri!tipoReporte, local!tipoReporte),
                                a!save(ri!documento, null),
                              },
                              disabled: if(
                               and(a!isNullOrEmpty(local!fechaF), a!isNullOrEmpty(local!fechaI), a!isNullOrEmpty(local!tipoReporte)),
                               true,
                               if(
                                 a!isNullOrEmpty(local!tipoReporte),
                                 false,
                                 if(
                                   and(a!isNotNullOrEmpty(local!fechaF), a!isNotNullOrEmpty(local!fechaI)),
                                   if(
                                     local!fechaF < local!fechaI,
                                     true,
                                     false,
                                   ),
                                   false
                                 )
                               )
                              ),
                              style: "DESTRUCTIVE",
                            )
                          },
                          align: "START",
                          marginAbove: "NONE"
                        )
                      }
                    )
                  }
                ),
              },
              style: "STANDARD",
              shape: "SEMI_ROUNDED",
              marginBelow: "STANDARD"
            )
          }
        )
      }
    ),

  }
)
