rule.definition:
a!localVariables(
  local!propiedades: dictutil_getkeys(
    cast(
      typeof(
        { key: "value" }
      ),
      ri!objetoViejo_any
    )
  ),
  local!modificacionesExistentesDataSubset: #"_a-0000e9a7-6d42-8000-e63f-01ef9001ef90_5247960"(
    idTabla_txt: ri!idTabla_txt,
    codigoLlave_txt: index(ri!objetoViejo_any, ri!propiedadId_txt)
  ),
  local!modificacionesExistentes: cast(
    typeof({'type!{urn:com:appian:types}cdo_modificaciones_r'()}),
    local!modificacionesExistentesDataSubset.data
  ),
  reject(
    fn!isnull,
    #"SYSTEM_SYSRULES_forEach"(
      local!propiedades,
      if(
        index(ri!objetoViejo_any, fv!item) <> index(ri!objetoNuevo_any, fv!item),
        'type!{urn:com:appian:types}cdo_modificaciones_r'(
          idTabla: ri!idTabla_txt,
          codigoLlave: index(ri!objetoViejo_any, ri!propiedadId_txt),
          campo: fv!item,
          numerosecuencia: a!localVariables(
            local!modificacionesDelCampo: index(
              local!modificacionesExistentes,
              wherecontains(
                fv!item, local!modificacionesExistentes.campo
              ),
              {}
            ),
            if (
              length(local!modificacionesDelCampo) > 0,
              max(local!modificacionesDelCampo.numeroSecuencia) + 1,
              1
            )
          ),
          valorPrevio: index(ri!objetoViejo_any, fv!item),
          valorNuevo: index(ri!objetoNuevo_any, fv!item),
          fechaYHora: now(),
          usuario: ri!usuario_txt
        ),
        null
      )
    )
  )
)
