          )
        }
      )
    }
  ),
  #"SYSTEM_SYSRULES_columnsLayout"(
    columns: {
      #"SYSTEM_SYSRULES_columnLayout"(
        contents: {
          #"SYSTEM_SYSRULES_checkboxField"(
            label: "",
            labelPosition: "ABOVE",
            choiceLabels: {"Es llamada telefónica (No tengo imagenes para la(s) operaciones)"},
