              ),
              #"SYSTEM_SYSRULES_columnLayout"(
                contents: {
                  #"SYSTEM_SYSRULES_ButtonArrayLayout"(
                    buttons: {
                      #"SYSTEM_SYSRULES_ButtonWidget"(
                        label: "Guardar",
                        style: "PRIMARY",
                        icon: "save",
                        size: "SMALL",
                        loadingIndicator: true,
                        disabled: if(
                          a!isNullOrEmpty(ri!bitacora),
                          true,
                          if(
                            and(ri!esImagenPendiente = true, a!isNullOrEmpty(ri!deal)),
                            true,
                            if(local!desactivar = true, true, false)
                          )
                        ),
                        saveInto: {
                          #"SYSTEM_SYSRULES_startProcess"(
                            processModel: #"_a-0000e9a7-6d42-8000-e63f-01ef9001ef90_4876437",
                            processParameters: {
                              datos: if(ri!esImagenPendiente = true, null, ri!datos),
                              ipremota: "",
