                              esImagenPendiente: ri!esImagenPendiente,
                              deal: ri!deal,
                              contrato: ri!contrato,
                              ejecutivo: ri!ejecutivo
                            },
                            onSuccess: {
                              a!save(ri!datos, fv!processInfo.pv.datos),
                              a!save(ri!deal, null),
                              a!save(ri!contrato, null),
                              a!save(ri!bitacora, null),
                              a!save(ri!refreshCount, ri!refreshCount +1),
                              a!save(ri!idReporteSeleccionado, null),
                            }
                          ), 
                        }
                      )
                    },
                    align: "START",
                    marginAbove: "STANDARD"
                  )
                }
              )
            },
            showWhen: if(and(isnull(ri!activo), local!desactivar = false),false,true)
          )
        }
      )
    }
  )
})
