interface.definition:
a!localVariables(
  local!prueba,
  local!error,
  {
    if(
      isnull(ri!documentoModificaciones),
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: {
            #"SYSTEM_SYSRULES_richTextIcon"(icon: "file-excel-o", size: "MEDIUM"),
            "Exportar Datos del Hist√≥rico"
          },
          link: #"SYSTEM_SYSRULES_dynamicLink"(
            saveInto: #"SYSTEM_SYSRULES_startProcess"(
              processModel: {
                #"_a-0000e9a7-6d42-8000-e63f-01ef9001ef90_5890147",
              },
              onSuccess: {
                a!save(
                  ri!documentoModificaciones, fv!processInfo.pv.documentoModificaciones
                )
              },

              onError: a!save(local!error, "No se puede generar el EXCEL")
            )
          ),
          linkStyle: "STANDALONE",
          style: "STRONG",
          color: "#cc0000"
        ),
        showWhen: ri!showWhen_bool,
      ),
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: {
            #"SYSTEM_SYSRULES_richTextIcon"(icon: "download", size: "MEDIUM"),
            "GENERAR REGISTRO ADF"
          },
          link: #"SYSTEM_SYSRULES_documentDownloadLink"(document: ri!documentoModificaciones),
          linkStyle: "STANDALONE",
          style: "STRONG",
          color: "#cc0000"
        ),
        showWhen: ri!showWhen_bool
      )
    )
  }
)
