interface.definition:
a!localVariables(
  /* Se Obtiene el reporte  */
  local!datos: #"_a-0000e982-d80b-8000-e63b-01ef9001ef90_4635413"(idreporte: ri!idReporteSeleccionado)[1],
  local!nombre_documento: concat(
    local!datos.contrato,
    "_",
    ri!ejecutivo.usuarioopics,
    "_",
    text(now(), "yyyy-MM-dd hh_mm_ss")
  ),
  /* Se obtienen los documentos por el segmento del ejecutivo */
  local!catalogoDelSegmento: if(
    a!isNullOrEmpty(
      #"_a-0000e968-30a1-8000-e639-01ef9001ef90_4493495"(
        idsegmento: 2,
        pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: - 1)
      )
    ),
    null,
    #"_a-0000e968-30a1-8000-e639-01ef9001ef90_4493570"(
      idsDocumentos: #"_a-0000e968-30a1-8000-e639-01ef9001ef90_4493495"(
        idsegmento: ri!ejecutivo.idsegmento,
        pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(startIndex: 1, batchSize: - 1)
      ).iddocumento
    ),

  ),
  /* 
  Depende de catalogoDelSegmento
  Se obtienen los documentos que sean CONTRATO
  */
  local!catDocContratoGeneral: if(
    a!isNullOrEmpty(local!catalogoDelSegmento),
    null,
    reject(
      fn!isnull,
      #"SYSTEM_SYSRULES_forEach"(
        items: local!catalogoDelSegmento,
        expression: if(
          and(
            fv!item.documentaa = "CONTRATO",
            /*if(*/
              /*a!isNotNullOrEmpty(fv!item.adf),*/
              /*tointeger(fv!item.adf) <> 1,*/
              /*true*/
            /*)*/
          ),
          fv!item,
          null
        )
      )
    ),

  ),
  /* 
  Depende de catalogoDelSegmento
  Se obtienen los documentos que sean CONTRATO y cuyo ADF sea igual a 1
  --Observacion
  Debido a que no se verifica que sean nulos en la parte de arriba se
  se obtendrian de ambos lugares documentos con ADF = 1
  */
  local!catDocADFGeneral: if(
    a!isNullOrEmpty(local!catalogoDelSegmento),
    null,
    reject(
      fn!isnull,
      #"SYSTEM_SYSRULES_forEach"(
        items: local!catalogoDelSegmento,
        expression: if(
          and(
            fv!item.documentaa = "CONTRATO",
            a!isNotNullOrEmpty(fv!item.adf) = 1
          ),
          fv!item,
          null
        )
      )
    ),

  ),
  /* 
  Depende de catalogoDelSegmento
  Se obtienen los documentos que sean OPERACION
  */
  local!catDocOperacionGeneral: if(
    a!isNullOrEmpty(local!catalogoDelSegmento),
    null,
    reject(
      fn!isnull,
      #"SYSTEM_SYSRULES_forEach"(
        items: local!catalogoDelSegmento,
        expression: if(
          fv!item.documentaa = "OPERACION",
          fv!item,
          null
        )
      )
    ),

  ),
  /* Se obtienen los documentos que ya han sido documentados y rechazados en digitalizados y rechazados */
  /*
  Obtiene los documentos aprobados
  */
  local!documentacionAceptados: if(
    a!isNullOrEmpty(local!catDocContratoGeneral),
    null,
    #"_a-0000e8b2-d897-8000-e5f1-01ef9001ef90_2372887"(
      contrato: local!datos.contrato,
      idsDocumentos: local!catDocContratoGeneral.iddocumento,
      estado: "A",
      /*usuariocargo:ri!ejecutivo.expediente*/
    ).data,

  ),
  /*
  Obtiene los documentos rechazados
  */
  local!documentacionRechazados: if(
    a!isNullOrEmpty(local!catDocContratoGeneral),
    null,
    #"_a-0000e982-d80b-8000-e63b-01ef9001ef90_4763710"(
      contrato: local!datos.contrato,
      idsDocumentos: local!catDocContratoGeneral.iddocumento
    )
  ),
  /*
  Depende de documentacionAceptados y catDocContratoGeneral
  Obtiene los documentos ACEPTADOS que esten en documentacionACeptada y 
  provengan de catDocContratoGeneral
  */
  local!catDocAceptadosDeContrato: if(
    a!isNullOrEmpty(local!documentacionAceptados),
    {},
    reject(
      fn!isnull,
      #"SYSTEM_SYSRULES_forEach"(
        items: local!catDocContratoGeneral,
        expression: if(
          contains(
            local!documentacionAceptados.iddocumento,
            fv!item.iddocumento
          ),
          fv!item,
          null
        )
      ),

    ),

  ),
  /*
  Depende de documentacionAceptados y catDocContratoGeneral
  Obtiene los documentos RECHAZADOS que esten en documentacionACeptada y 
  provengan de catDocContratoGeneral
  */
  local!catDocRechazadosDeContrato: if(
    a!isNullOrEmpty(local!documentacionRechazados),
    {},
    reject(
      fn!isnull,
      #"SYSTEM_SYSRULES_forEach"(
        items: local!catDocContratoGeneral,
        expression: if(
          contains(
            local!documentacionRechazados.iddocumento,
            fv!item.iddocumento
          ),
          fv!item,
          null
        )
      )
    )
  ),

  /* Usa los documentos cuyo ADF = 1 y obtiene los doctos fisicos*/
  local!adfsAceptados: if(
    a!isNullOrEmpty(local!catDocADFGeneral),
    {},
    #"_a-0000e982-d80b-8000-e63b-01ef9001ef90_4749073"(
      idsDocumentos: local!catDocADFGeneral.iddocumento,
      contrato: local!datos.contrato
    ),

  ),
  /*
  Depende de adfsAceptados y catDocADFGeneral
  obtiene de catDocADFGeneral los documentos que esten  en adfsAceptados
  */
  local!catDocParaADFAceptado: if(
    a!isNullOrEmpty(local!adfsAceptados),
    {},
    reject(
      fn!isnull,
      #"SYSTEM_SYSRULES_forEach"(
        items: local!catDocADFGeneral,
        expression: if(
          contains(
            local!adfsAceptados.iddocumento,
            fv!item.iddocumento
          ),
          fv!item,
          null
        )
      ),

    ),

  ),

  local!observacion,
  /* Obtiene documentos aprovados de OPERACION */
  local!digitalizadosAceptados: if(
    a!isNullOrEmpty(local!catDocOperacionGeneral),
    null,
    #"_a-0000e916-edcb-8000-e60c-01ef9001ef90_3117428"(
      idsDocumentos: local!catDocOperacionGeneral,
      contrato: local!datos.contrato,
      operacion: local!datos.operacion,
      estado: "A"
    )
  ),
  /*Filtra para obtener los documentos de operacion aprobados que esten en
  el segmento de catDocOperacionGeneral*/
  local!catDocAceptadoDeOperacion: if(
    a!isNullOrEmpty(local!digitalizadosAceptados),
    {},
    reject(
      fn!isnull,
      #"SYSTEM_SYSRULES_forEach"(
        items: local!catDocOperacionGeneral,
        expression: if(
          contains(
            local!digitalizadosAceptados.iddocumento,
            fv!item.iddocumento
          ),
          fv!item,
          null
        )
      )
    )
  ),
  local!digitalizadosRechazados: if(
    a!isNullOrEmpty(local!catDocOperacionGeneral),
    null,
    #"_a-0000e982-d80b-8000-e63b-01ef9001ef90_4776405"(
      operacion: local!datos.operacion,
      contrato: local!datos.contrato,
      idsDocumentos: local!catDocOperacionGeneral.iddocumento
    )
  ),
  local!catDocRechazadosDeOperacion: if(
    a!isNullOrEmpty(local!digitalizadosRechazados),
    {},
    reject(
      fn!isnull,
      #"SYSTEM_SYSRULES_forEach"(
        items: local!catDocOperacionGeneral,
        expression: if(
          contains(
            local!digitalizadosRechazados.iddocumento,
            fv!item.iddocumento
          ),
          fv!item,
          null
        )
      )
    )
  ),
  /*Obtiene los documentos faltantes de operaci√≥n*/
  local!catDocFaltantesDeOperacion: if(
    a!isNullOrEmpty(local!catDocOperacionGeneral),
    {},
    #"_a-0000eb53-0a0e-8000-e689-01ef9001ef90_9534907"(
      arreglo_1:local!catDocOperacionGeneral,
      arreglo_2:local!catDocAceptadoDeOperacion,
      arreglo_3:local!catDocRechazadosDeOperacion
    )
    /*difference(*/
      /*local!catDocOperacionGeneral,*/
      /*local!catDocAceptadoDeOperacion,*/
      /*local!catDocRechazadosDeOperacion*/
    /*)*/
  ),
  /*  Se crean las variables para los dropdowns*/

  local!documentoAceptadoADF: if(
    a!isNullOrEmpty(local!catDocParaADFAceptado),
    {},
    local!catDocParaADFAceptado.iddocumento
  ),
  local!valorescontrato,
  local!valoresADF,
  local!documentoperacion: if(
    a!isNullOrEmpty(local!catDocAceptadoDeOperacion),
    {},
    local!catDocAceptadoDeOperacion.iddocumento
  ),
  local!valoresoperaciones,
  local!verDetallesAceptado: false,
  local!idDocumentoSeleccionado,
  local!documentoSeleccionado,
  local!documentoRechazadoSeleccionado,
  local!digitalizadoSeleccionado,
  local!digitalizadoRechazadoSeleccionado,
  local!auxiliar_rechazado_operacion,
  /*-----------------*/
  /*
  Depende de catDocAceptadosDeContrato y catDocParaADFAceptado
  */
  local!solo_aceptados_contrato: 
  #"_a-0000eb53-0a0e-8000-e689-01ef9001ef90_9534907"(
    arreglo_1:local!catDocAceptadosDeContrato,
    arreglo_2: local!catDocParaADFAceptado
  ),
  /*difference(*/
    /*local!catDocAceptadosDeContrato,*/
    /*local!catDocParaADFAceptado*/
  /*),*/
  local!auxiliar_aceptado_contrato: if(
    a!isNullOrEmpty(local!solo_aceptados_contrato),
    null,
    tointeger(
      local!solo_aceptados_contrato.iddocumento
    )
  ),

  local!solo_rechazados_contrato: local!catDocRechazadosDeContrato,
  local!auxiliar_rechazado_contrato,

  local!estan_en_documentacion_pendientes_contrato: if(
    a!isNullOrEmpty(local!catDocContratoGeneral),
    null,
    #"_a-0000e8b2-d897-8000-e5f1-01ef9001ef90_2372887"(
      contrato: local!datos.contrato,
      idsDocumentos: local!catDocContratoGeneral.iddocumento,
      estado: "P"
    ).data,

  ),
  local!catestan_en_documentacion_pendientes_contrato: if(
    a!isNullOrEmpty(local!estan_en_documentacion_pendientes_contrato),
    {},
    reject(
      fn!isnull,
      #"SYSTEM_SYSRULES_forEach"(
        items: local!catDocContratoGeneral,
        expression: if(
          contains(
            local!estan_en_documentacion_pendientes_contrato.iddocumento,
            fv!item.iddocumento
          ),
          fv!item,
          null
        )
      ),

    ),

  ),
  local!faltan_contrato: 
  if(a!isNotNullOrEmpty(local!catDocContratoGeneral),
  #"_a-0000eb53-0a0e-8000-e689-01ef9001ef90_9534907"(
    arreglo_1:local!catDocContratoGeneral,
    arreglo_2:local!catestan_en_documentacion_pendientes_contrato,
    arreglo_3:local!catDocAceptadosDeContrato
  ),
  /*difference(*/
    /*local!catDocContratoGeneral,*/
    /*local!catestan_en_documentacion_pendientes_contrato,*/
    /*local!catDocAceptadosDeContrato*/
  /*),*/

  null),


  local!auxiliar_faltan_contrato,

  /*-----------------*/
  if(
    a!isNullOrEmpty(local!catalogoDelSegmento),
    {
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: {
          #"SYSTEM_SYSRULES_richTextIcon"(
            icon: "warning",
            color: "NEGATIVE",
            size: "MEDIUM"
          ),
          #"SYSTEM_SYSRULES_richTextItem_v1"(
            text: " El segmento no tiene cat√°logos de documentos asociados, comun√≠quese con su el admininstrador para solucionar este problema",
            color: "NEGATIVE",
            size: "MEDIUM",
            style: "STRONG"
          )
        }
      )
    },
    {
      #"_a-0000e968-30a1-8000-e639-01ef9001ef90_4429544"(
        bitacora: local!observacion,
        activo: ri!activo,
        datos: local!datos,
        refreshCount: ri!refreshCount,
        startIndex: ri!startIndex,
        batchSize: ri!batchSize,
        idReporteSeleccionado: ri!idReporteSeleccionado,
        esImagenPendiente: false
      ),
      #"SYSTEM_SYSRULES_columnsLayout"(
        columns: {
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_richTextDisplayField"(
                value: {
                  #"SYSTEM_SYSRULES_richTextItem_v1"(text: "Documentos por Contrato "),
                  #"SYSTEM_SYSRULES_richTextIcon"(icon: "arrow-down")
                }
              ),

              if(
                a!isNotNullOrEmpty(local!solo_aceptados_contrato),
                #"SYSTEM_SYSRULES_checkboxField"(
                  label: "",
                  labelPosition: "ABOVE",
                  choiceLabels: #"SYSTEM_SYSRULES_forEach"(
                    local!solo_aceptados_contrato,
                    if(
                      true,
                      fv!item.documento & " - " & "(Aceptado)",
                      ""
                    )
                  ),

                  choiceValues: local!solo_aceptados_contrato.iddocumento,
                  value: local!auxiliar_aceptado_contrato,
                  saveInto: local!auxiliar_aceptado_contrato,
                  disabled: true,
                  validations: {}
                ),
                {}
              ),
              if(
                a!isNotNullOrEmpty(local!catDocParaADFAceptado),
                #"SYSTEM_SYSRULES_checkboxField"(
                  label: "",
                  labelPosition: "ABOVE",

                  choiceLabels: #"SYSTEM_SYSRULES_forEach"(
                    local!catDocParaADFAceptado,
                    if(a!isNotNullOrEmpty(local!catDocAceptadosDeContrato),
                    if(
                      contains(
                        local!catDocAceptadosDeContrato,
                        fv!item
                      ),
                      fv!item.documento & " - " & "(Aceptado)" & " - " & "üìú",
                      fv!item.documento & " - " & "üìú",

                    ),
                    fv!item.documento & " - " & "üìú",
                    )

                  ),
                  choiceValues: local!catDocParaADFAceptado.iddocumento,
                  value: local!documentoAceptadoADF,
                  saveInto: local!documentoAceptadoADF,
                  disabled: true,
                  validations: {}
                ),
                {}
              ),

              if(
                a!isNotNullOrEmpty(local!solo_rechazados_contrato),
                #"SYSTEM_SYSRULES_checkboxField"(
                  label: "",
                  labelPosition: "ABOVE",

                  choiceLabels: #"SYSTEM_SYSRULES_forEach"(
                    local!solo_rechazados_contrato,

                    fv!item.documento & " - " & "(Rechazado)"

                  ),

                  choiceValues: local!solo_rechazados_contrato.iddocumento,
                  value: local!auxiliar_rechazado_contrato,
                  saveInto: local!auxiliar_rechazado_contrato,
                  disabled: a!isNotNullOrEmpty(ri!activo),
                  validations: {}
                ),
                {}
              ),
              /*ultimos print de checkbox*/
              if(
                a!isNotNullOrEmpty(local!faltan_contrato),
                #"SYSTEM_SYSRULES_checkboxField"(
                  label: "",
                  labelPosition: "ABOVE",
                  choiceLabels: local!faltan_contrato.documento,
                  choiceValues: local!faltan_contrato.iddocumento,
                  value: local!auxiliar_faltan_contrato,
                  saveInto: local!auxiliar_faltan_contrato,
                  disabled: a!isNotNullOrEmpty(ri!activo),
                  validations: {}
                ),
                {}
              ),

            }
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_richTextDisplayField"(
                value: {
                  #"SYSTEM_SYSRULES_richTextItem_v1"(text: "Documentos por Operaci√≥n "),
                  #"SYSTEM_SYSRULES_richTextIcon"(icon: "arrow-down")
                }
              ),
              if(
                a!isNotNullOrEmpty(local!catDocAceptadoDeOperacion),
                #"SYSTEM_SYSRULES_checkboxField"(
                  label: "",
                  labelPosition: "ABOVE",

                  choiceLabels: #"SYSTEM_SYSRULES_forEach"(
                    local!catDocAceptadoDeOperacion,

                    fv!item.documento & " - " & "(Aceptado)"

                  )
                  ,
                  choiceValues: local!catDocAceptadoDeOperacion.iddocumento,
                  value: local!documentoperacion,
                  saveInto: local!documentoperacion,
                  disabled: true,
                  validations: {}
                ),
                {}
              ),
              if(
                a!isNotNullOrEmpty(local!catDocRechazadosDeOperacion),
                #"SYSTEM_SYSRULES_checkboxField"(
                  label: "",
                  labelPosition: "ABOVE",

                  choiceLabels: #"SYSTEM_SYSRULES_forEach"(
                    local!catDocRechazadosDeOperacion,
                    if(
                      true,
                      fv!item.documento & " - " & "(Rechazado)",
                      ""
                    )
                  ),
                  choiceValues: local!catDocRechazadosDeOperacion.iddocumento,
                  value: local!auxiliar_rechazado_operacion,
                  saveInto: local!auxiliar_rechazado_operacion,
                  disabled: a!isNotNullOrEmpty(ri!activo),
                  validations: {}
                ),
                {}
              ),
              if(
                a!isNotNullOrEmpty(local!catDocFaltantesDeOperacion),
                #"SYSTEM_SYSRULES_checkboxField"(
                  label: "",
                  labelPosition: "ABOVE",
                  choiceLabels: local!catDocFaltantesDeOperacion.documento,
                  choiceValues: local!catDocFaltantesDeOperacion.iddocumento,
                  value: local!valoresoperaciones,
                  saveInto: local!valoresoperaciones,
                  disabled: a!isNotNullOrEmpty(ri!activo),
                  validations: {}
                ),
                {}
              )
            }
          ),
          #"SYSTEM_SYSRULES_columnLayout"(
            contents: {
              #"SYSTEM_SYSRULES_cardLayout"(
                contents: {
                  #"SYSTEM_SYSRULES_richTextDisplayField"(
                    value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                      text: "Informaci√≥n de los documentos",
                      size: "MEDIUM",
                      color: "ACCENT",
                      style: { "STRONG", "EMPHASIS" }
                    ),
                    align: "CENTER"
                  ),
                  if(
                    and(
                      a!isNullOrEmpty(local!catDocAceptadoDeOperacion),
                      a!isNullOrEmpty(local!catDocAceptadosDeContrato)
                    ),
                    #"SYSTEM_SYSRULES_richTextDisplayField"(
                      value: {
                        #"SYSTEM_SYSRULES_richTextItem_v1"(
                          text: "No se han subido documentos para este reporte",
                          color: "NEGATIVE"
                        )
                      },
                      align: "CENTER"
                    ),
                    {
                      #"SYSTEM_SYSRULES_columnsLayout"(
                        columns: {
                          #"SYSTEM_SYSRULES_columnLayout"(
                            contents: {
                              #"SYSTEM_SYSRULES_richTextDisplayField"(
                                value: {
                                  #"SYSTEM_SYSRULES_richTextItem_v1"(text: "Documentos por Contrato "),
                                  #"SYSTEM_SYSRULES_richTextIcon"(icon: "arrow-down")
                                }
                              ),
                              if(
                                a!isNotNullOrEmpty(local!catDocAceptadosDeContrato),
                                {
                                  #"SYSTEM_SYSRULES_forEach"(
                                    items: local!catDocAceptadosDeContrato,
                                    expression: #"SYSTEM_SYSRULES_richTextDisplayField"(
                                      value: {
                                        #"SYSTEM_SYSRULES_richTextItem_v1"(text: concat(fv!item.documento, " "), ),
                                        #"SYSTEM_SYSRULES_richTextIcon"(
                                          icon: "check-circle",
                                          color: "POSITIVE",
                                          caption: "Mostrar Detalles",
                                          linkStyle: "STANDALONE",
                                          link: #"SYSTEM_SYSRULES_dynamicLink"(
                                            value: true,
                                            saveInto: {
                                              local!verDetallesAceptado,
                                              a!save(
                                                local!idDocumentoSeleccionado,
                                                fv!item.iddocumento
                                              ),
                                              a!save(
                                                local!documentoRechazadoSeleccionado,
                                                null
                                              ),
                                              a!save(
                                                local!digitalizadoRechazadoSeleccionado,
                                                null
                                              ),
                                              a!save(local!digitalizadoSeleccionado, null),
                                              a!save(
                                                local!documentoSeleccionado,
                                                a!flatten(
                                                  reject(
                                                    fn!isnull,
                                                    #"SYSTEM_SYSRULES_forEach"(
                                                      items: local!documentacionAceptados,
                                                      expression: if(
                                                        tointeger(fv!item.iddocumento) = local!idDocumentoSeleccionado,
                                                        fv!item,
                                                        null
                                                      )
                                                    )
                                                  )
                                                )
                                              ),

                                            },

                                          )
                                        )
                                      }
                                    )
                                  ),

                                },
                                #"SYSTEM_SYSRULES_richTextDisplayField"(
                                  value: #"SYSTEM_SYSRULES_richTextItem_v1"(
                                    text: "No se ha documentado por contrato"
                                  ),
                                  showWhen: and(
                                    a!isNullOrEmpty(local!catDocAceptadosDeContrato),
                                    a!isNullOrEmpty(local!catDocRechazadosDeOperacion)
                                  )
                                )
                              ),
                              if(
                                a!isNotNullOrEmpty(local!catDocRechazadosDeContrato),
                                #"SYSTEM_SYSRULES_forEach"(
                                  items: local!catDocRechazadosDeContrato,
                                  expression: #"SYSTEM_SYSRULES_richTextDisplayField"(
                                    value: {
                                      #"SYSTEM_SYSRULES_richTextItem_v1"(text: concat(fv!item.documento, " "), ),
                                      #"SYSTEM_SYSRULES_richTextIcon"(
                                        icon: "times-circle",
                                        color: "NEGATIVE",
                                        caption: "Mostrar Detalles",
                                        linkStyle: "STANDALONE",
                                        link: #"SYSTEM_SYSRULES_dynamicLink"(
                                          value: true,
                                          saveInto: {
                                            local!verDetallesAceptado,
                                            a!save(
                                              local!idDocumentoSeleccionado,
                                              fv!item.iddocumento
                                            ),
                                            a!save(local!documentoSeleccionado, null),
                                            a!save(
                                              local!digitalizadoRechazadoSeleccionado,
                                              null
                                            ),
                                            a!save(local!digitalizadoSeleccionado, null),
                                            a!save(
                                              local!documentoRechazadoSeleccionado,
                                              a!flatten(
                                                reject(
                                                  fn!isnull,
                                                  #"SYSTEM_SYSRULES_forEach"(
                                                    items: local!documentacionRechazados,
                                                    expression: if(
                                                      tointeger(fv!item.iddocumento) = local!idDocumentoSeleccionado,
                                                      fv!item,
                                                      null
                                                    )
                                                  )
                                                )
                                              )
                                            ),

                                          },

                                        )
                                      )
                                    }
                                  )
                                ),
                                {}
                              )
                            }
                          ),
                          #"SYSTEM_SYSRULES_columnLayout"(
                            contents: {
                              #"SYSTEM_SYSRULES_richTextDisplayField"(
                                value: {
                                  #"SYSTEM_SYSRULES_richTextItem_v1"(text: "Documentos por Operaci√≥n "),
                                  #"SYSTEM_SYSRULES_richTextIcon"(icon: "arrow-down")
                                },

                              ),
                              if(
                                a!isNotNullOrEmpty(local!catDocAceptadoDeOperacion),
                                #"SYSTEM_SYSRULES_forEach"(
                                  items: local!catDocAceptadoDeOperacion,
                                  expression: #"SYSTEM_SYSRULES_richTextDisplayField"(
                                    value: {
                                      #"SYSTEM_SYSRULES_richTextItem_v1"(text: fv!item.documento & " ", ),
                                      #"SYSTEM_SYSRULES_richTextIcon"(
                                        icon: "check-circle",
                                        caption: "Mostrar Detalles",
                                        color: "POSITIVE",
                                        linkStyle: "STANDALONE",
                                        link: #"SYSTEM_SYSRULES_dynamicLink"(
                                          value: true,
                                          saveInto: {
