interface.definition:
a!localVariables(
  local!usuariosPorSegmento,
  local!ccostosNoBD,
  local!centrosPorRegion,
  local!usuarioSeleccionado,

  {
    if(
      isnull(ri!documento_doc),
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: {
            #"SYSTEM_SYSRULES_richTextIcon"(icon: "file-excel-o", size: "MEDIUM"),
            "GENERAR EXCEL"
          },
          link: #"SYSTEM_SYSRULES_dynamicLink"(
            saveInto: {
              a!save(
                local!ccostosNoBD,
                if(
                  a!isNullOrEmpty(
                    #"_a-0000e9f5-96e6-8000-e641-01ef9001ef90_6351963"(ccosto: null)
                  ),
                  null,
                  #"_a-0000e9f5-96e6-8000-e641-01ef9001ef90_6351963"(ccosto: null).cc
                )
              ),
              #"SYSTEM_SYSRULES_startProcess"(
                processModel: #"_a-0000ea2e-319c-8000-e644-01ef9001ef90_6808403",
                processParameters: {
                  ccostos: ri!ccostos,
                  contrato: ri!contrato,
                  FechaDividida: ri!fechaDividida,
                  Fechal: ri!fechaFin,
                  Fecha: ri!fechaInicio,
                  FechaalDividida: ri!fechaalDividida,
                  estado: ri!estado,
                  estadocc: ri!estadoCC,
                  iddocumento: ri!catDoc,
                  imagen:ri!imagen
                },
                onSuccess: {
                  a!save(
                    ri!documento_doc,
                    fv!processInfo.pv.documento
                  )
                },
                onError: "No se puede generar el EXCEL"
              )
            }
          ),
          linkStyle: "STANDALONE",
          style: "STRONG",
          color: "#cc0000"
        )
      ),
      #"SYSTEM_SYSRULES_richTextDisplayField"(
        value: #"SYSTEM_SYSRULES_richTextItem_v1"(
          text: {
            #"SYSTEM_SYSRULES_richTextIcon"(icon: "download", size: "MEDIUM"),
            "DESCARGAR EXCEL"
          },
          link: #"SYSTEM_SYSRULES_documentDownloadLink"(document: ri!documento_doc),
          linkStyle: "STANDALONE",
          style: "STRONG",
          color: "#cc0000"
        )
      )

    )
  }
)
