rule.definition:
a!localVariables(
  local!totalCount: readexcelsheetpaging(
    excelDocument: ri!documento,
    sheetNumber: 0,
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, -1),
    numberOfColumns: 0
  ).totalCount,
  local!informacion: readexcelsheetpaging(
    excelDocument: ri!documento,
    sheetNumber: 0,
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, local!totalCount),
    numberOfColumns: 0
  ).data,
  local!operaciones: reject(
    fn!isnull,
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!informacion,
        expression: {
          if(
            fv!index = 1,
            null(),
            fv!item.values[ri!posExcelOperacion]
          )
        }
      )
    ),
  ),
  local!contratos: reject(
    fn!isnull,
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!informacion,
        expression: {
          if(
            fv!index = 1,
            null(),
            if(
              a!isNullOrEmpty(fv!item.values[ri!posExcelContrato]),
              null,
              fv!item.values[ri!posExcelContrato]
              /*if(*/
                /*len(fv!item.values[ri!posExcelContrato]) < 11,*/
                /*rule!SCDO_insertarCerosParaContrato(contrato: fv!item.values[ri!posExcelContrato], ceros: 11 - len(fv!item.values[ri!posExcelContrato])),*/
                /*fv!item.values[ri!posExcelContrato]*/
              /*)*/
            )
          )
        }
      )
    ),
  ),
  local!igual_cantidad: if(
    and(length(local!operaciones) = length(local!contratos), length(local!operaciones) = local!totalCount - 1, ri!tipoReporte = 1),
    true,
    if(
      and(length(local!operaciones) = length(local!contratos), length(local!operaciones) = local!totalCount - 1, ri!tipoReporte = 2),
      true,
      false
    )
  ),
  local!igual_cantidad

)
