rule.definition:
a!localVariables(
  local!operaciones:#"SYSTEM_SYSRULES_forEach"(
    items: ri!texto, 
    expression: {
      a!localVariables(
        local!datosFila: split(fv!item, "|"),
        local!operacion: index(local!datosFila,2, null),
        if( /*and(*/
          a!isNotNullOrEmpty(local!operacion)/*,rule!SCDO_HAY_LETRAS(texto:local!operacion)=false)*/,
        local!operacion,
        null
        )

      )

    } 
  ),
  local!contratos:#"SYSTEM_SYSRULES_forEach"(
    items: ri!texto, 
    expression: {
      a!localVariables(
        local!datosFila: split(fv!item, "|"),
        local!contrato: index(local!datosFila,3, null),
        if(and(len(local!contrato)=<11,a!isNotNullOrEmpty(local!contrato)/*,rule!SCDO_HAY_LETRAS(texto:local!contrato)=false*/),
        local!contrato,
        null
        )

      )

    } 
  ),
  /*count( reject(fn!isnull, local!operaciones))=ri!total_lineas,*/
  and(count(reject(fn!isnull, local!operaciones))=ri!total_lineas,count(reject(fn!isnull, local!contratos))=ri!total_lineas)



)
