rule.definition:
a!localVariables(
  local!totalCount: index(readexcelsheetpaging(
    excelDocument: ri!documento,
    sheetNumber: 0,
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, 0),
    numberOfColumns: 0
  ),"totalCount", null),
  local!informacion: index(readexcelsheetpaging(
    excelDocument: ri!documento,
    sheetNumber: 0,
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(1, local!totalCount),
    numberOfColumns: 0
  ),"data",null),

  local!contratos: reject(
    fn!isnull,
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!informacion,
        expression: {
          if(
            fv!index = 1,
            null(),
            if(
              a!isNullOrEmpty(index(fv!item, "values", null)[1]),
              null,
              index(fv!item, "values", null)[1]

            )
          )
        }
      )
    ),
  ),
  local!adfs: reject(
    fn!isnull,
    a!flatten(
      #"SYSTEM_SYSRULES_forEach"(
        items: local!informacion,
        expression: {
          if(
            fv!index = 1,
            null(),
            if(
              a!isNullOrEmpty(index(fv!item, "values", null)[2]),
              null,
              index(fv!item, "values", null)[2]

            )
          )
        }
      )
    ),
  ),
 local!mapa:if( 
    and(
      length(local!contratos)=local!totalCount-1,
    length(local!contratos) = length(local!adfs)),
  {a!map(contratos: local!contratos),a!map(adfs:local!adfs)},
  null
  ),
  local!mapa


)
