  /*proceso de guardar*/
  /*-------*/
  /*Se obtienen los documentos que ya estan registrados para el contrato en imagenes pendientes */
  /*local!documentos: rule!SCDO_get_imagenesPendientesByFilters(contrato: ri!contrato),*/
  /*local!documentacion_existente:if(a!isNotNullOrEmpty(rule!SCDO_getDocumentosByFilter(contrato:ri!contrato).data),*/
  /*rule!SCDO_getDocumentosByFilter(contrato:ri!contrato).data.iddocumento,null),*/
  /*local!observacion,*/
  local!deal: "0000000",
  /*local!documentoADF,*/
  /*local!documentocontrato,*/
  local!documento,
  /*----------*/
  /* Se obtiene los catalogos que pertenecen al segmento en la tabla intermedia doctos_segmento */
  local!catalogoDelSegmento: if(
