rule.definition:
a!localVariables(
  local!devolver: 
  if(
    and(
      a!isNullOrEmpty(ri!arreglo_1),
      a!isNullOrEmpty(ri!arreglo_2),
      a!isNullOrEmpty(ri!arreglo_3),),
      null,
      if(
        and(
          a!isNullOrEmpty(ri!arreglo_1),
          a!isNullOrEmpty(ri!arreglo_2)
        ),
        ri!arreglo_3,
        if(
          and(
            a!isNullOrEmpty(ri!arreglo_1),
            a!isNullOrEmpty(ri!arreglo_3)
          ),
          ri!arreglo_2,
          if(
            and(
              a!isNullOrEmpty(ri!arreglo_2),
              a!isNullOrEmpty(ri!arreglo_3)
            ),
            ri!arreglo_1,
            if(and(
              a!isNullOrEmpty(ri!arreglo_1),
              and(
                a!isNotNullOrEmpty(ri!arreglo_2),
                a!isNotNullOrEmpty(ri!arreglo_3)
              )),
              difference(ri!arreglo_2, ri!arreglo_3),
              if(and(
                a!isNullOrEmpty(ri!arreglo_2),
                and(
                  a!isNotNullOrEmpty(ri!arreglo_1),
                  a!isNotNullOrEmpty(ri!arreglo_3)
                )),
                difference(ri!arreglo_1, ri!arreglo_3),
                if(and(
                  a!isNullOrEmpty(ri!arreglo_3),
                  and(
                    a!isNotNullOrEmpty(ri!arreglo_1),
                    a!isNotNullOrEmpty(ri!arreglo_2)
                  )),
                  difference(ri!arreglo_1, ri!arreglo_2),
                  difference(ri!arreglo_1, ri!arreglo_2, ri!arreglo_3)
                )
              )
            )
          )
        )
      )

  ),
  local!devolver
)
